var index=parent.layer.getFrameIndex(window.name);$(function(){$("#Back").click(function(){parent.layer.close(index)})});function DoSubmitForm(b,c){try{$(b).valid()&&$.post(c,$(b).serialize(),function(a){200==a.statusCode?(window.parent.refreshPage(),parent.layer.close(index)):swal({title:"\u63d0\u793a",text:a.message,type:"error"})},"json")}catch(a){swal({title:"\u63d0\u793a",text:a.message,type:"error"})}}function refreshPage(){$(".btn-primary").click()}function ShowPage(b,c,a){layer.open({type:2,area:c,title:a,fixed:!1,maxmin:!0,content:b})}function DoAction(b,c){swal({title:"\u63d0\u793a",text:c,type:"warning",showCancelButton:!0,confirmButtonColor:"#DD6B55",confirmButtonText:"\u786e\u5b9a",cancelButtonText:"\u53d6\u6d88",closeOnConfirm:!1},function(){$.ajax({url:b,type:"post",success:function(a){200==a.statusCode?swal({title:"\u6210\u529f",text:a.message,type:"success",showCancelButton:!1,confirmButtonColor:"#DD6B55",confirmButtonText:"\u786e\u5b9a",closeOnConfirm:!0},function(){refreshPage()}):swal("\u5931\u8d25",a.message,"error")}})})};